import React from 'react';
import styles from '../styles/home.module.scss';
import 'antd/dist/antd.css';
import {Link} from "react-router-dom";
import { Menu, Icon } from 'antd';
import { Anchor } from 'antd';

const { LinkAnchor } = Anchor;
const { SubMenu } = Menu;

export default class Home extends React.Component {

  scrollToAnchor (id){
    document.getElementById(id).scrollIntoView({block: "start", behavior: "smooth"});
  };

  isWeChat = function () {
    var browser = {
        versions: function () {
            var u = navigator.userAgent, app = navigator.appVersion;
            return {         //ÁßªÂä®ÁªàÁ´ØÊµèËßàÂô®ÁâàÊú¨‰ø°ÊÅØ
                trident: u.indexOf('Trident') > -1, //IEÂÜÖÊ†∏
                presto: u.indexOf('Presto') > -1, //operaÂÜÖÊ†∏
                webKit: u.indexOf('AppleWebKit') > -1, //ËãπÊûú„ÄÅË∞∑Ê≠åÂÜÖÊ†∏
                gecko: u.indexOf('Gecko') > -1 && u.indexOf('KHTML') == -1, //ÁÅ´ÁãêÂÜÖÊ†∏
                mobile: !!u.match(/AppleWebKit.*Mobile.*/), //ÊòØÂê¶‰∏∫ÁßªÂä®ÁªàÁ´Ø
                ios: !!u.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/), //iosÁªàÁ´Ø
                android: u.indexOf('Android') > -1 || u.indexOf('Linux') > -1, //androidÁªàÁ´ØÊàñucÊµèËßàÂô®
                iPhone: u.indexOf('iPhone') > -1, //ÊòØÂê¶‰∏∫iPhoneÊàñËÄÖQQHDÊµèËßàÂô®
                iPad: u.indexOf('iPad') > -1, //ÊòØÂê¶iPad
                webApp: u.indexOf('Safari') == -1 //ÊòØÂê¶webÂ∫îËØ•Á®ãÂ∫èÔºåÊ≤°ÊúâÂ§¥ÈÉ®‰∏éÂ∫ïÈÉ®
                    };
       }(),
       language: (navigator.browserLanguage || navigator.language).toLowerCase()
    };

    var ua = navigator.userAgent.toLowerCase();//Ëé∑ÂèñÂà§Êñ≠Áî®ÁöÑÂØπË±°
    return (browser.versions.mobile && ua.match(/MicroMessenger/i) == "micromessenger") ;
  }

  downloadAndriod = function () {
    // update android version here ONLY
    var apkVersion = 'squirrel-release-1.0.0-93.apk';
    var apkURL = 'https://d1c658sv1e2p6j.cloudfront.net/resources/release/android/' + apkVersion + '?t=' + new Date().getTime();
    window.open(apkURL, '_self');
  };

  downloadWindows = function () {
     // update fileVersion ONLY when updating
     var fileVersion = 'win/SquirrelVPN-1.0.0-10-x64-release.exe';
     var fileURL = 'https://d1c658sv1e2p6j.cloudfront.net/resources/release/' + fileVersion + '?t=' + new Date().getTime();
     window.open(fileURL, '_self');
  };
  downloadMac = function () {
    // update fileVersion ONLY when updating
    var fileVersion = 'mac/SquirrelVPN-1.0.0-10-x64-release.dmg';
    var fileURL = 'https://d1c658sv1e2p6j.cloudfront.net/resources/release/' + fileVersion + '?t=' + new Date().getTime();
    window.open(fileURL, '_self');

  };

  openTestflight = function() {
    window.open('https://testflight.apple.com/join/OwwczVyR', '_self');
  }




  render() {
        return (
            <div className={styles.container}>
                <div className={styles.content_container}>
                    <Menu mode="horizontal">
                        <Menu.Item key="home">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Home
                          </a>
                        </Menu.Item>
                        <Menu.Item key="download">
                          <a href="javascript:;" onClick={()=>this.scrollToAnchor('download_section')} rel="noopener noreferrer">
                            Download
                          </a>
                        </Menu.Item>
                        <Menu.Item key="buy">
                          <Link to="/buy">
                            <a href="javascript:;" target="_blank" rel="noopener noreferrer">
                              Buy
                            </a>
                          </Link>
                        </Menu.Item>
                        <Menu.Item key="services">
                          <a href="javascript:;" onClick={()=>this.scrollToAnchor('services_section')} rel="noopener    noreferrer">
                              Services
                          </a>
                        </Menu.Item>
                        <Menu.Item key="support">
                          <a href="javascript:;" onClick={()=>this.scrollToAnchor('support_section')} rel="noopener noreferrer">
                            Support
                          </a>
                        </Menu.Item>
                    </Menu>

                    <div className={styles.website_title} >
                        <h1>SquirrelVPN:First Decentralized VPN</h1>
                        <h3>Enjoy the true internet, your last VPN service</h3>
                        <Link to="/buy"><button className={styles.btn_buy}>Buy</button></Link>
                        <button className={styles.btn_buy} onClick={()=>this.scrollToAnchor('download_section')}>Download</button>
                        <div className={styles.app_demo_image}></div>
                    </div>

                    <div className={styles.website_title} id="services_section">
                        <h1>VPN Services</h1>
                        <div className={styles.vpn_features_image}> </div>
                        <Link to="/buy"><button className={styles.btn_buy}>Buy</button></Link>
                    </div>

                    <div className={styles.technology_section}>
                       <h1>Technology</h1>
                       <h3> ‚ö°Ô∏èSquirrelVPN is a secure VPN powered by a decentralized tokenized P2P network potentially allowing your own nodes to join to do bandwidth mining.üí•

                       The underlying blockchain system is under development to tokenize your bandwidth contribution.

                       üåàEventually, SquirrelVPN will be the Airbnb version of bandwidth market that delivers highly secure and private internet access with low cost. </h3>

                    </div>

                    <div className={styles.download_section} id="download_section">
                        <h1>How To Download</h1>
                        <div className={styles.download_panel}>
                            <h2>01. Download the Mobile App</h2>
                            <h4>Andriod</h4>
                            <button onClick={()=>this.downloadAndriod()} className={styles.btn_download}>SquirrelVPN for Android</button>
                            ‚Äã<p className={styles.light}>File size: 20.5M</p>
                            <div className={styles.separator}>---</div>
                            <h4>iOS</h4>
                            <p>SquirrelVPN Pro (TestFlight)</p>
                            <button className={styles.btn_download} onClick={()=>this.openTestflight()} >SquirrelVPN Pro</button>
                            <div className={styles.pro_qr}><img src={require('../images/testflight-pro.png')}/></div>
                            <p>SquirrelVPN (App Store)</p>
                            <Link to="/buy"><button className={styles.btn_download}>Squirrel for iOS</button></Link>
                            <div className={styles.app_store_qr}><img src={require('../images/all-in-one.png')}/></div>
                        </div>
                        <div className={styles.download_panel}>
                            <h2>02. Download the Desktop App</h2>
                            <p>Please make sure you have an Squirrel VPN account, you can only sign up in our Mobile App.</p>
                            <h4>Mac</h4>
                            <button  onClick={()=>this.downloadMac()} className={styles.btn_download}>SquirrelVPN for MacOS</button>
                            ‚Äã<p className={styles.light}>MacOS 10.14‚Äã + <br/>
                            File size: 31M</p>
