import React from 'react';
import styles from '../styles/home.module.scss';
import 'antd/dist/antd.css';
import {Link} from "react-router-dom";
import { Menu, Icon } from 'antd';
import { Anchor } from 'antd';

const { LinkAnchor } = Anchor;
const { SubMenu } = Menu;

export default class Home extends React.Component {
  urls = {
    current: 'mail',
  };

  handleClickMenu = e => {
    console.log('click ', e);
    this.setState({
      current: e.key,
    });

  };

  scrollToAnchor (id){
    document.getElementById(id).scrollIntoView({block: "start", behavior: "smooth"});
  };

  isWeChat = function () {
    var browser = {
        versions: function () {
            var u = navigator.userAgent, app = navigator.appVersion;
            return {         //ç§»åŠ¨ç»ˆç«¯æµè§ˆå™¨ç‰ˆæœ¬ä¿¡æ¯
                trident: u.indexOf('Trident') > -1, //IEå†…æ ¸
                presto: u.indexOf('Presto') > -1, //operaå†…æ ¸
                webKit: u.indexOf('AppleWebKit') > -1, //è‹¹æœã€è°·æ­Œå†…æ ¸
                gecko: u.indexOf('Gecko') > -1 && u.indexOf('KHTML') == -1, //ç«ç‹å†…æ ¸
                mobile: !!u.match(/AppleWebKit.*Mobile.*/), //æ˜¯å¦ä¸ºç§»åŠ¨ç»ˆç«¯
                ios: !!u.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/), //iosç»ˆç«¯
                android: u.indexOf('Android') > -1 || u.indexOf('Linux') > -1, //androidç»ˆç«¯æˆ–ucæµè§ˆå™¨
                iPhone: u.indexOf('iPhone') > -1, //æ˜¯å¦ä¸ºiPhoneæˆ–è€…QQHDæµè§ˆå™¨
                iPad: u.indexOf('iPad') > -1, //æ˜¯å¦iPad
                webApp: u.indexOf('Safari') == -1 //æ˜¯å¦webåº”è¯¥ç¨‹åºï¼Œæ²¡æœ‰å¤´éƒ¨ä¸åº•éƒ¨
                    };
       }(),
       language: (navigator.browserLanguage || navigator.language).toLowerCase()
    };

    var ua = navigator.userAgent.toLowerCase();//è·å–åˆ¤æ–­ç”¨çš„å¯¹è±¡
    return (browser.versions.mobile && ua.match(/MicroMessenger/i) == "micromessenger") ;
  }
  downloadAndriod = function () {

         // update version here ONLY for android
      var apkVersion = 'squirrel-release-1.0.0-93.apk';
      var apkURL = 'https://d1c658sv1e2p6j.cloudfront.net/resources/release/android/' + apkVersion + '?t=' + new Date().getTime();
      if (browser.versions.mobile) {//åˆ¤æ–­æ˜¯å¦æ˜¯ç§»åŠ¨è®¾å¤‡æ‰“å¼€ã€‚
          var ua = navigator.userAgent.toLowerCase();//è·å–åˆ¤æ–­ç”¨çš„å¯¹è±¡
          if (ua.match(/MicroMessenger/i) == "micromessenger") {
              //åœ¨å¾®ä¿¡ä¸­æ‰“å¼€
              document.getElementById("tipscover").style.visibility = "visible";
              document.getElementById("instructionsContent").style.visibility = "visible";
          } else {
              // open in browser
              window.open(apkURL, '_self');
          }
      } else {
          //PCæµè§ˆå™¨æ‰“å¼€
          window.open(apkURL, '_self');
      }
  };



  render() {
        return (
            <div className={styles.container}>
                <div className={styles.content_container}>
                    <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
                        <Menu.Item key="home">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Home
                          </a>
                        </Menu.Item>
                        <Menu.Item key="download">
                          <a href="javascript:;" onClick={()=>this.scrollToAnchor('download_section')} rel="noopener noreferrer">
                            Download
                          </a>
                        </Menu.Item>
                        <Menu.Item key="buy">
                          <Link to="/buy">
                            <a href="javascript:;" target="_blank" rel="noopener noreferrer">
                              Buy
                            </a>
                          </Link>
                        </Menu.Item>
                        <Menu.Item key="services">
                          <a href="javascript:;" onClick={()=>this.scrollToAnchor('services_section')} rel="noopener    noreferrer">
                              Services
                          </a>
                        </Menu.Item>
                        <Menu.Item key="support">
                          <a href="javascript:;" onClick={()=>this.scrollToAnchor('support_section')} rel="noopener noreferrer">
                            Support
                          </a>
                        </Menu.Item>
                    </Menu>

                    <div className={styles.website_title} >
                        <h1>SquirrelVPN:First Decentralized VPN</h1>
                        <h3>Enjoy the true internet, your last VPN service</h3>
                        <Link to="/buy"><button className={styles.btn_buy}>Buy</button></Link>
                        <button className={styles.btn_buy} onClick={()=>this.scrollToAnchor('download_section')}>Download</button>
                        <div className={styles.app_demo_image}></div>
                    </div>

                    <div className={styles.website_title} id="services_section">
                        <h1>VPN Services</h1>
                        <div className={styles.vpn_features_image}> </div>
                        <Link to="/buy"><button className={styles.btn_buy}>Buy</button></Link>
                    </div>

                    <div className={styles.technology_section}>
