import React from 'react';
import styles from '../styles/home.module.scss';
import 'antd/dist/antd.css';
import {Link} from "react-router-dom";
import { Menu, Icon } from 'antd';
import { Anchor } from 'antd';

const { LinkAnchor } = Anchor;
const { SubMenu } = Menu;

export default class Home extends React.Component {
  state = {
    current: 'mail',
  };

  handleClickMenu = e => {
    console.log('click ', e);
    this.setState({
      current: e.key,
    });

//   $("#services-section").click(function() {
//     $("html, body").animate({
//     scrollTop: $("#services-section").offset().top }, {duration: 500,easing: "swing"});
//     return false;
//  });
  };

  render() {
        return (
            <div className={styles.main_container}>
                <div className={styles.container}>
                    <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
                        <Menu.Item key="home">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Home
                          </a>
                        </Menu.Item>
                        <Menu.Item key="download">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Download
                          </a>
                        </Menu.Item>
                        <Menu.Item key="buy">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Buy
                          </a>
                        </Menu.Item>
                        <Menu.Item key="services">

                            <a>Services</a>



                        </Menu.Item>
                        <Menu.Item key="support">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Support
                          </a>
                        </Menu.Item>
                    </Menu>

                    <div className={styles.website_title}>
                        <h1>SquirrelVPN:First Decentralized VPN</h1>
                        <h3>Enjoy the true internet, your last VPN service</h3>
                        <Link to="/buy"><button className={styles.btn_buy}>Buy</button></Link>
                        <Link to="/download"><button className={styles.btn_buy}>Download</button></Link>
                        <div className={styles.app_demo_image}></div>
                    </div>

                    <div id="services-section" className={styles.website_title}>
                        <h1>VPN Services</h1>
                        <div className={styles.vpn_features_image}> </div>
                        <Link to="/buy"><button className={styles.btn_buy}>Buy</button></Link>
                    </div>

                    <div className={styles.download_section}>
                        <h1>How To Download</h1>
                        <div className={styles.download_panel}>
                            <h2>01. Download the Mobile App</h2>
                            <h4>Andriod</h4>
                            <Link to="/buy"><button className={styles.btn_download}>SquirrelVPN for Android</button></Link>
                            â€‹<p className={styles.light}>File size: 20.5M</p>
                            <div>---</div>
                            <h4>iOS</h4>
                            <p>SquirrelVPN Pro (TestFlight)</p>
                            <Link to="/buy"><button className={styles.btn_download}>SquirrelVPN Pro</button></Link>

                            <p>SquirrelVPN (App Store)</p>
                            <Link to="/buy"><button className={styles.btn_download}>Squirrel for iOS</button></Link>

                        </div>
                    </div>

                </div>
            </div>

        );
    }



}
