import React from 'react';
import styles from '../styles/home.module.scss';
import 'antd/dist/antd.css';
import {Link} from "react-router-dom";
import { Menu, Icon } from 'antd';
import { Anchor } from 'antd';

const { LinkAnchor } = Anchor;

const { SubMenu } = Menu;

export default class Home extends React.Component {
  state = {
    current: 'mail',
  };

  handleClickMenu = e => {
    console.log('click ', e);
    this.setState({
      current: e.key,
    });

//   $("#services-section").click(function() {
//     $("html, body").animate({
//     scrollTop: $("#services-section").offset().top }, {duration: 500,easing: "swing"});
//     return false;
//  });
  };

  render() {
        return (
            <div className={styles.main_container}>
                <div className={styles.container}>
                    <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
                        <Menu.Item key="home">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Home
                          </a>
                        </Menu.Item>
                        <Menu.Item key="download">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Download
                          </a>
                        </Menu.Item>
                        <Menu.Item key="buy">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Buy
                          </a>
                        </Menu.Item>
                        <Menu.Item key="services">


                        </Menu.Item>
                        <Menu.Item key="support">
                          <a href="https://ant.design" target="_blank" rel="noopener noreferrer">
                            Support
                          </a>
                        </Menu.Item>
                    </Menu>

                <Anchor affix={false}>
                  <LinkAnchor href="#services-section" title="Services"/>

                </Anchor>
                    <div className={styles.website_title}>
                        <h1>SquirrelVPN:First Decentralized VPN</h1>
                        <h3>Enjoy the true internet, your last VPN service</h3>
                        <Link to="/buy"><button className={styles.btn_buy}>Buy</button></Link>
                        <Link to="/download"><button className={styles.btn_buy}>Download</button></Link>
                        <div className={styles.app_demo_image}></div>
                    </div>

                    <a name="services-section" > here</a>
                    <div name="services-section" className={styles.website_title}>
                        <h1>VPN Services</h1>
                        <div className={styles.vpn_features_image}> </div>
                        <Link to="/buy"><button className={styles.btn_buy}>Buy</button></Link>
                    </div>

                </div>
            </div>

        );
    }



}
