import React from 'react';
import styles from '../styles/user-center.module.scss';
import withdrawStyles from '../styles/withdraw.module.scss';
import 'antd/dist/antd.css';
import {Row, Col, message, Icon, Tabs, List, Drawer, Alert, Spin, notification} from 'antd';
import WebServerRestClient from "../tools/WebServerRestClient";
import StorageUtils from "../tools/StorageUtils";
import UserComponent from "../components/UserComponent";
import CommonUtils from "../tools/CommonUtils";
const { TabPane } = Tabs;

export default class Home extends UserComponent {

    constructor(props) {
        super(props);
        this.state = {
            showDrawer: false,
            showWithdrawDrawer: false,
            dataList: [],
            allList: [],
            balance: 0,
            levelOneList: [],
            levelThreeList: [],
            levelTwoList: [],
            totalAmount: null,
            totalUsers: 0,
            withdrawList: [],
            withdrawAmount: 0.00,
            withdrawFee: 0.00,
            withdrawInfo: {}
        }
    }

    componentDidMount() {
        this.checkUserParams();
        this.loadTradeList();
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        this.setState({[name]: value});

        if (name === 'withdrawAmount') {
            if (this.state.feeFlag) {
                let withdrawFee = value * this.state.withdrawFeeScale;
                this.setState({withdrawFee: withdrawFee})
            }
        }
    }

    handleInputFocus(event) {
        const target = event.target;
        if (target.type === 'text' || target.type === 'number') {
            this.setState({showMessageDialog: false});
        }
    }

    checkUserParams(){
        new WebServerRestClient().POST('/user/check-user-params', {
            userId: window.CurrentUser.userId
        }).then(response => {
            console.log("check-user-params response -> " + JSON.stringify(response));
            if (response.code == 200) {
                // user parameters is ok
            } else if (response.code == 101) {
                this.props.history.push('/binding-wallet');
            } else if (response.code == 102) {
                this.props.history.push('/binding-code');
            } else if (response.code == 103) {
                this.props.history.push('/payment');
            } else if (response.code == 401 || response.code == 403) {
                message.warn("未登录！");
                this.props.history.push('/login');
            } else {
                message.warn(response['msg'])
            }
        }).catch(err => {
            message.error("Error: " + err.message);
        });
    }

    loadTradeList(){
        new WebServerRestClient().GET('/user/query-user-earnings', {
            userId: window.CurrentUser.userId
        }).then(response => {
            console.log("query-user-earnings response -> " + JSON.stringify(response));
            if (response.code == 200) {
                // Object.assign(this.state, params);
                this.setState(response.data);
            } else if (response.code == 401 || response.code == 403) {
                message.warn("未登录！");
                this.props.history.push('/login');
            } else {
                message.warn(response['msg'])
            }
        }).catch(err => {
            message.error("Error: " + err.message);
        });
    }


    tabsChanged(key){
        let keys = key.split('-');
        const allList = this.state.allList;
        let dataList = [];
        for (let j = 0; j < keys.length; j++) {
            for (let i = 0; i < allList.length; i++) {
                if (allList[i].tradeType == keys[j]) {
                    dataList.push(allList[i]);
                }
            }
        }
        this.setState({dataList: dataList});
    }

    startWithdraw() {
        new WebServerRestClient().GET('/user/query-withdraw-params', {
            userId: window.CurrentUser.userId,
            currency: 'ETH',
        }).then(response => {
            console.log("query-withdraw-params response -> " + JSON.stringify(response));
            this.setState({doing: false});
            if (response.code == 200) {
                this.setState({
                    feeFlag: response.data.feeFlag,
                    withdrawMinAmount: response.data.withdrawMinAmount,
                    withdrawFeeScale: response.data.withdrawFeeScale,
                    withdrawFeeScaleString: response.data.withdrawFeeScaleString,
                    withdrawFreeTime: response.data.withdrawFreeTime,
                    withdrawFreeDays: response.data.withdrawFreeDays,
                });
                if (response.data.doingFlag) {
                    this.setState({showWithdrawInfoDrawer: true});
                    this.setState({
                        withdrawTime: response.data.withdrawTime,
                        withdrawInfo: response.data.withdrawInfo
                    });
                } else {
                    this.setState({showWithdrawDrawer: true});
                }
            } else if (response.code == 401 || response.code == 403) {
                message.warn("未登录！");
                this.props.history.push('/login');
            } else {
                message.warn(response['msg']);
            }
        }).catch(err => {
            this.setState({doing: false});
            message.error("Error: " + err.message);
        });
    }

    confirmWithdraw() {
        let withdrawAmount = this.state.withdrawAmount;
        if (withdrawAmount === 0) {
            this.setState({showMessageDialog: true, message: "请输入提币数量"});
            return false;
        }
        this.setState({doing: true});
        new WebServerRestClient().POST('/user/create-withdraw-order', {
            userId: window.CurrentUser.userId,
            currency: 'ETH',
            amount: this.state.withdrawAmount
        }).then(response => {
            console.log("create-withdraw-order response -> " + JSON.stringify(response));
            this.setState({doing: false});
            if (response.code == 200) {
                this.openNotificationWithIcon('success', '系统提示', '提币申请已提交，2-3个工作日到账');
                this.setState({
                    showWithdrawDrawer: false,
                    withdrawAmount: 0.00,
                    withdrawFee: 0.00
                });
                document.getElementById('withdrawAmount').value = '';
            } else if (response.code == 401 || response.code == 403) {
                message.warn("未登录！");
                this.props.history.push('/login');
            } else {
                this.setState({showMessageDialog: true, message: response['msg']});
            }
        }).catch(err => {
            this.setState({doing: false});
            message.error("Error: " + err.message);
        });
    }

    openNotificationWithIcon = (type, message, description) => {
        notification[type]({
            placement: 'bottomRight',
            message: message,
            description: description,
        });
    };

    render() {
        return (
            <div className={styles.container}>
                <div className={styles.header}>
                    <div className={styles.header_account}>
                        <img className={styles.logo} src={require('../images/logo_home.png')}/>
                        <div className={styles.info}>
                            <div className={styles.email}>{window.CurrentUser.email}</div>
                            <div className={styles.address}>ETH钱包：{CommonUtils.cutEthAddress(window.CurrentUser.ethAddress)}</div>
                        </div>
                        <Icon type="menu" className={styles.menu} onClick={()=>this.setState({showDrawer: true})} />
                    </div>
                    <div className={styles.header_earnings}>
                        <Row>
                            <Col span={8}>
                                <div className={styles.earnings_label}>总收益</div>
                                <div className={styles.earnings_value}>{this.state.totalAmount}</div>
                            </Col>
                            <Col span={8}>
                                <div className={styles.balance_title}>余额</div>
                                <div className={styles.balance_eth}>{this.state.balance} ETH</div>
                                <div className={styles.earnings_value}>
                                    <button className={styles.btn_withdraw} onClick={this.startWithdraw.bind(this)}>提币</button>
                                </div>
                            </Col>
                            <Col span={8}>
                                <div className={styles.earnings_label}>总客户</div>
                                <div className={styles.earnings_value}>{this.state.totalUsers}人</div>
                            </Col>
                        </Row>
                    </div>
                </div>
                <div className={styles.list_area}>
                    <Tabs tabBarGutter={0}
                          defaultActiveKey={'all'}
                          className={styles.tabs}
                          tabBarStyle={{marginBottom: '0px',borderBottom: '1px solid #f5f5f5'}}
                          onChange={this.tabsChanged.bind(this)}>
                        <TabPane tab="全部" key="all">
                            <List
                                dataSource={this.state.allList}
                                renderItem={item => (this.renderItem(item))}>
                            </List>
                        </TabPane>
                        <TabPane tab="第一级" key="1">
                            <List
                                dataSource={this.state.dataList}
                                renderItem={item => (this.renderItem(item))}>
                            </List>
                        </TabPane>
                        <TabPane tab="第二级" key="2">
                            <List
                                dataSource={this.state.dataList}
                                renderItem={item => (this.renderItem(item))}>
                            </List>
                        </TabPane>
                        <TabPane tab="第三级" key="3">
                            <List
                                dataSource={this.state.dataList}
                                renderItem={item => (this.renderItem(item))}>
                            </List>
                        </TabPane>
                        <TabPane tab="提币" key="11-12">
                            <List
                                dataSource={this.state.dataList}
                                renderItem={item => (this.renderItem(item))}>
                            </List>
                        </TabPane>
                    </Tabs>
                </div>

                <Drawer
                    getContainer={document.getElementById('root')}
                    placement="bottom"
                    height={130}
                    closable={false}
                    onClose={()=>this.setState({showDrawer: false})}
                    visible={this.state.showDrawer}>
                    <div className={styles.menu_item} onClick={() => {
                        this.props.history.push('/binding-wallet?from=/user-center');
                    }}>修改钱包地址</div>
                    <div className={styles.menu_item_line}></div>
                    <div className={styles.menu_item} onClick={() => {
                        StorageUtils.remove(window.Constants.CURRENT_USER);
                        this.props.history.push('/login');
                    }}>退出登录
                    </div>
                </Drawer>


                <Drawer
                    getContainer={document.getElementById('root')}
                    placement="bottom"
                    height={400}
                    closable={false}
                    className={withdrawStyles.withdraw}
                    onClose={() => this.setState({showWithdrawDrawer: false})}
                    visible={this.state.showWithdrawDrawer}>
                    <div className="drawer_close">
                        <Icon type="close" onClick={()=>this.setState({showWithdrawDrawer: false})}/>
                    </div>
                    <div className={withdrawStyles.step_title}><Icon type="upload" className={styles.icon_up}/>提现到个人钱包：</div>
                    <div className={withdrawStyles.step_desc}>
                        收款钱包地址： {CommonUtils.cutEthAddress(window.CurrentUser.ethAddress)}<br/>
                        最小提币数量： {this.state.withdrawMinAmount}<br/>
                        {
                            this.state.feeFlag ?
                                <div>
                                    手续费率： <span style={{color: '#c758d6'}}>{this.state.withdrawFeeScaleString}</span> (间隔{this.state.withdrawFreeDays}日免手续费) <br/>
                                    免费提币时间： {this.state.withdrawFreeTime}之后<br/>
                                </div>
                                :
                                null
                        }

                    </div>
                    <input type={'number'} id={'withdrawAmount'} name={'withdrawAmount'}
                           className={withdrawStyles.form_input}
                           placeholder={'提币数量'}
                           onFocus={this.handleInputFocus.bind(this)}
                           onChange={this.handleInputChange.bind(this)}/>
                    <div className={withdrawStyles.tips}>
                        <Row>
                            <Col span={12}>
                                手续费：{CommonUtils.toDecimal(this.state.withdrawFee, 6)}
                            </Col>
                            <Col span={12}>
                                <div style={{textAlign: 'right'}}>
                                    实际到账：{CommonUtils.toDecimal(this.state.withdrawAmount - this.state.withdrawFee, 6)}
                                </div>
                            </Col>
                        </Row>
                    </div>
                    {this.state.showMessageDialog ?
                        <div className={withdrawStyles.message_box}>
                            <Alert message={this.state.message} type="warning" closable={true}
                                   onClose={() => {
                                       this.setState({showMessageDialog: false})
                                   }}/>
                        </div>
                        :
                        null
                    }
                    <div className={withdrawStyles.form_button_row}>
                        <button className={withdrawStyles.form_button} onClick={this.confirmWithdraw.bind(this)}>
                            {this.state.doing ?
                                <Spin indicator={<Icon type="loading" className='icon_loading' spin/>}/>
                                :
                                '确认提币'
                            }
                        </button>
                    </div>
                </Drawer>

                <Drawer
                    getContainer={document.getElementById('root')}
                    placement="bottom"
                    height={400}
                    closable={false}
                    className={withdrawStyles.withdraw}
                    onClose={() => this.setState({showWithdrawInfoDrawer: false})}
                    visible={this.state.showWithdrawInfoDrawer}>
                    <div className="drawer_close">
                        <Icon type="close" onClick={()=>this.setState({showWithdrawInfoDrawer: false})}/>
                    </div>
                    <div className={withdrawStyles.step_title}><Icon type="upload" className={styles.icon_up}/>提现到个人钱包：</div>
                    <div className={withdrawStyles.step_desc}>
                        代币类型： {this.state.withdrawInfo.currency}    <br/>
                        钱包地址： {CommonUtils.cutEthAddress(this.state.withdrawInfo.receiptAddress)}    <br/>
                        提币时间：{this.state.withdrawTime}    <br/>
                        提币数量： {this.state.withdrawInfo.orderAmount}    <br/>
                        手续费： {this.state.withdrawInfo.fee}    <br/>
                        <span style={{color: '#637bfd'}}>实际到账： {this.state.withdrawInfo.amount}</span>
                    </div>
                    <div className={withdrawStyles.doing_div}>
                        <button className={withdrawStyles.doing_button}>处理中...</button>
                        <div className={withdrawStyles.tips}>2-3个工作日到账</div>
                    </div>
                </Drawer>

            </div>
        );
    }

    renderItem(item){
        return(
            <div className={styles.trade}>
                <div className={styles.left}>
                    <div className={styles.icon_block}>
                        {
                            item.tradeDirection === '+' ?
                            <Icon type="download" className={styles.icon_down}/>
                            :
                            <Icon type="upload" className={styles.icon_up}/>
                        }
                    </div>
                    <div className={styles.left_block}>
                        <div className={styles.title}>
                            {this.render_trade_type(item.tradeType)}
                        </div>
                        <div className={styles.target}>
                            From:&nbsp;{this.render_from(item)}
                        </div>
                    </div>
                </div>
                <div className={styles.right}>
                    <div className={item.tradeDirection === '+' ? styles.amount_up : styles.amount_down}>
                        {item.tradeDirection}{item.earningsAmount}
                    </div>
                    <div className={styles.time}>{item.paymentTime}</div>
                </div>
            </div>
        )
    }

    render_trade_type(tradeType) {
        if(tradeType == 1 || tradeType == 2 || tradeType == 3)
            return '挖矿返现(第'+tradeType+'级)'
        if (tradeType == 11)
            return '提币';
        else if (tradeType == 12)
            return '提币手续费';
    }

    render_from(item){
        if(item.tradeType == 1 || item.tradeType == 2 || item.tradeType == 3)
            return item.email;
        else
            return '松鼠加速器';
    }

}
